import React from "react";
import { Route } from "react-router-dom";
import {createStructuredSelector} from 'reselect'
import { connect } from "react-redux";

import CollectionOverviewContainer from '../../components/collections-overview/collections-overview.container'
import CollectionPageContainer from '../collection/collection.container';
import ItemProfilePageContainer from '../item-profile/item-profile.container'
import ItemProfile from "../item-profile/item-profile.component";

import { fetchCollectionsStartAsync } from "../../redux/shop/shop.actions";
import { selectIsCollectionsLoaded} from '../../redux/shop/shop.selectors'

import WithSpinner from "../../components/with-spinner/with-spinner.component";

const ItemProfileWithSpinner = WithSpinner(ItemProfile);

class ShopPage extends React.Component {  


  componentDidMount() {
   const {fetchCollectionsStartAsync} = this.props;
   fetchCollectionsStartAsync();
  }

  render() {
    const { match, isCollectionsLoaded } = this.props;

    return (
      <div className='shop-page'>
        <Route
          exact
          path={`${match.path}`}
          component={CollectionOverviewContainer}
        />

        <Route
          exact
          path={`${match.path}/:collectionId`}
          component={CollectionPageContainer}
        />

        <Route
          path={`${match.path}/:collectionId/:linkUrl`}
          component={ItemProfilePageContainer}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  isCollectionsLoaded: selectIsCollectionsLoaded 
})

const mapDispatchToProps = (dispatch) => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
