import React from "react";
import { Route } from "react-router-dom";
import {createStructuredSelector} from 'reselect'
import { connect } from "react-redux";

import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import CollectionPage from "../collection/collection.component";
import ItemProfile from "../item-profile/item-profile.component";

import { fetchCollectionsStartAsync } from "../../redux/shop/shop.actions";
import {selectIsCollectionFetching} from '../../redux/shop/shop.selectors'

import WithSpinner from "../../components/with-spinner/with-spinner.component";

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionsPageWithSpinner = WithSpinner(CollectionPage);
const ItemProfileWithSpinner = WithSpinner(ItemProfile);

class ShopPage extends React.Component {  
  componentDidMount() {

  }

  render() {
    const { match } = this.props;

    return (
      <div className='shop-page'>
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <CollectionsOverviewWithSpinner isLoading={loading} />
          )}
        />

        <Route
          exact
          path={`${match.path}/:collectionId`}
          render={(props) => (
            <CollectionsPageWithSpinner isLoading={loading} {...props} />
          )}
        />

        <Route
          path={`${match.path}/:collectionId/:linkUrl`}
          render={(props) => (
            <ItemProfileWithSpinner isLoading={loading} {...props} />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  isCollectionFetching: selectIsCollectionFetching 
})

const mapDispatchToProps = (dispatch) => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(null, mapStateToProps, mapDispatchToProps)(ShopPage);
